- #### top（拓扑排序）
```cpp
#include<iostream>
#include<stack>
#include<vector>
using namespace std;

vector<int> mp[100005], tp;

bool top( int n, int m, int* du ){
	stack<int> st;
	int cnt = 0;
	for( int i=1; i<=n; ++i ) if(du[i]==0) st.push(i);
	while( st.size() ){
		int tmp = st.top();
		tp.push_back(tmp);
		st.pop();
		for( int node : mp[tmp] ){
			if(--du[node]==0) st.push(node);
		}
		cnt++;
	}
	return cnt==n;
}

int main(){
	int n, m, du[100005]={},v1,v2;
	cin >> n >> m;
	for( int i=1; i<=m; ++i ){
		cin >> v1 >> v2;
		du[v2]++;
		mp[v1].push_back(v2);
	}
	if( top(n,m,du) )
		for( int x:tp ) cout << x << " ";
	else cout << " has cicle";
	return 0;
}

```

